<td>{{ relay.from }}</td>
<td>{{ relay.by }}</td>
<td>{{ relay.with }}</td>
<td>{{ relay.date }}</td>
<td>{{ relay.blacklisted }}</td>
</tr>
{% endfor %}
</tbody>
</table>

{% endif %}","Include Case Data":false,"Template":"{"Content":"","ContentTemplateName":"","HtmlTemplateName":""}"}

