<html>

<head>
<style>
html[data-theme="light"] {
        --background-color: hsl(0, 0%, 100%);
        --text-color: hsl(237, 12%, 35%);
      }
      html[data-theme="dark"] {
        --background-color: hsl(240, 16%, 16%);
        --text-color: hsl(240, 4%, 71%);
      }
      html[data-theme="dark-gray"] {
        --background-color: hsl(221, 35%, 20%);
        --text-color: hsl(216, 45%, 84%);
      }
body {
    background: var(--background-color);
        color: var(--text-color);
        font-family: "Open Sans", "sans-serif";
  height:  100%;
  width:  100%;
  padding-top: 50px;
  padding-right: 30px;
  padding-bottom: 50px;
  padding-left: 80px;
}

</style>
<script type="text/javascript">
      dataTheme = window.parent.document
        .getElementsByTagName("html")[0]
        .getAttribute("data-theme");
      document.documentElement.setAttribute("data-theme", dataTheme);
      document.documentElement.classList.add("smp-theme-" + dataTheme);
    </script>
</head>

<body>

{%- set ok_img = "data:image/png;base64," -%}

{%- set fail_img = "data:image/png;base64," -%}

{% set data_set = [EmailUtilities_Analyze Headers.JsonResult] %}

<div class="dmarc-compliance">

<table width="100%">
<col style="width:20%"><col style="width:80%">

    <tr>
      <td>
{%- if data_set.DMARC is defined -%}
        {%- if data_set.DMARC['valid'] -%}
        <img title="compliant" src="{{ ok_img }}" width="17" />
        {%- else -%}
        <img title="compliant" src="{{ fail_img }}" width="17" />
        {%- endif -%}
{%- else -%}
 <img title="compliant" src="{{ fail_img }}" width="17" />
{%- endif -%}
      </td>
      <td>DMARC Compliant</td>
    </tr>

    <tr>
      <td>
        {%- if 'tags' in data_set.DMARC and data_set.DMARC['tags']['aspf'] == "r" -%}
            {%- if data_set.FromDomain == data_set.MFromDomain or data_set.FromParentDomain == data_set.MFromDomain  -%}
        <img title="spf_align" src="{{ ok_img }}" width="17" />
            {%- else -%}
        <img title="spf_align" src="{{ fail_img }}" width="17" />
            {%- endif -%}
        {%- else -%}
            {%- if data_set.FromDomain == data_set.MFromDomain -%}
        <img title="spf_align" src="{{ ok_img }}" width="17" />
            {%- else -%}
        <img title="spf_align" src="{{ fail_img }}" width="17" />    
            {%- endif -%}
        {%- endif -%}
      </td>
      <td>SPF Alignment</td>
    </tr>
    <tr>

      <td>
      {%- if data_set.SPF  -%}
        {%- if data_set.SPF['Auth'] == True -%}
        <img title="spf_auth" src="{{ ok_img }}" width="17" />
        {%- else -%}
        <img title="spf_auth" src="{{ fail_img}}" width="17" />
        {%- endif -%}
        {%- else -%}
        <img title="spf_auth" src="{{ fail_img}}" width="17" />
        {%- endif -%}
      </td>
      <td>SPF Authenticated</td>
    </tr>
    <tr>

      <td>
        {%- if data_set.StrongSPF == True -%}
        <img title="spf_auth" src="{{ ok_img }}" width="17" />
        {%- else -%}
        <img title="spf_auth" src="{{ fail_img}}" width="17" />
        {%- endif -%}
      </td>
      <td>Strong SPF Record</td>
    </tr>
    <tr>

      <td>
        {%- if data_set.DKIMVerify == True -%}
        <img title="dkim_align" src="{{ ok_img }}" width="17" />
        {%- else -%}
        <img title="dkim_align" src="{{ fail_img }}" width="17" />
        {%- endif -%}
      </td>
      <td>DKIM Alignment</td>
    </tr>
    <tr>

      <td>
      {%- if data_set.ARCVerify -%}
        {%- if data_set.ARCVerify[result] == True -%}
        <img title="dkim_align" src="{{ ok_img }}" width="17" />
        {%- else -%}
        <img title="dkim_align" src="{{ fail_img }}" width="17" />
        {%- endif -%}
        {% else %}
        <img title="dkim_align" src="{{ fail_img }}" width="17" />
        {%- endif -%}
      </td>
      <td>ARC Verify</td>
    </tr>
  </table>
</div>

</body>
</html>","Include Case Data":false,"Template":"{"Content":"","ContentTemplateName":"","HtmlTemplateName":""}"}

